// New Chat UI with drawer menu and modern layout + Leave Chat option

import React, { useState, useEffect } from 'react';
import socket from './socket';

function Chat() {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');
  const [isJoined, setIsJoined] = useState(false);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState({});
  const [participants, setParticipants] = useState([]);
  const [showSidebar, setShowSidebar] = useState(false);

  const joinRoom = () => {
    if (username.trim() && room.trim()) {
      socket.emit('join_room', { room, username });
      setIsJoined(true);
    }
  };

  const sendMessage = () => {
    if (message.trim()) {
      const msgData = { sender: username, room, message };
      socket.emit('send_message', msgData);
      setMessage('');
    }
  };

  const leaveChat = () => {
    setIsJoined(false);
    setUsername('');
    setRoom('');
    setMessage('');
    setMessages({});
    setParticipants([]);
    socket.disconnect();
    window.location.reload();
  };

  useEffect(() => {
    socket.on('receive_message', (data) => {
      const { room: msgRoom, sender, message } = data;
      setMessages((prev) => {
        const roomMessages = prev[msgRoom] || [];
        return {
          ...prev,
          [msgRoom]: [...roomMessages, { sender, message, time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) }],
        };
      });
    });

    socket.on('user_list', (userList) => {
      setParticipants(userList);
    });

    return () => {
      socket.off('receive_message');
      socket.off('user_list');
    };
  }, []);

  if (!isJoined) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <h2 className="text-2xl font-bold mb-4">Join a Chat Room</h2>
        <input
          className="border p-2 m-2 w-64"
          placeholder="Your Name"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          className="border p-2 m-2 w-64"
          placeholder="Room Name"
          value={room}
          onChange={(e) => setRoom(e.target.value)}
        />
        <button className="bg-purple-500 text-white px-4 py-2 rounded" onClick={joinRoom}>Join</button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      <div className="flex justify-between items-center bg-gradient-to-r from-indigo-500 to-purple-500 text-white px-6 py-4">
        <h2 className="text-xl font-bold">Chat Room: {room}</h2>
        <div className="flex items-center gap-2">
          <button
            className="bg-white text-purple-600 font-semibold py-1 px-3 rounded shadow"
            onClick={() => setShowSidebar(!showSidebar)}
          >
            â˜° Menu
          </button>
        </div>
      </div>

      <div className="flex flex-1">
        {/* Message Section */}
        <div className="flex-1 p-4 overflow-y-auto flex flex-col">
          {(messages[room] || []).map((msg, idx) => (
            <div
              key={idx}
              className={`mb-3 p-4 rounded-lg shadow w-fit ${msg.sender === username ? 'ml-auto bg-purple-100' : 'bg-white'}`}
            >
              <div className="font-semibold text-indigo-600">{msg.sender}</div>
              <div>{msg.message}</div>
              <div className="text-xs text-gray-400 text-right">{msg.time}</div>
            </div>
          ))}
        </div>

        {/* Sidebar (toggleable) */}
        {showSidebar && (
          <div className="w-64 bg-white border-l p-4 shadow-md">
            <h3 className="font-bold text-lg mb-2">Online Users ({participants.length})</h3>
            <ul className="space-y-1">
              {participants.map((p, i) => (
                <li key={i} className="flex items-center gap-2 text-sm">
                  <span className="w-2 h-2 bg-green-500 rounded-full"></span>
                  {p === username ? `${p} (You)` : p}
                </li>
              ))}
            </ul>
            <button
              className="mt-6 bg-red-500 text-white px-4 py-2 rounded w-full"
              onClick={leaveChat}
            >
              Leave Chat
            </button>
          </div>
        )}
      </div>

      <div className="flex p-4 bg-white border-t items-center">
        <input
          className="flex-1 border border-gray-300 rounded-full px-4 py-2 text-sm mr-2 focus:outline-none focus:ring"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button
          className="bg-gradient-to-r from-indigo-400 to-purple-400 text-white px-4 py-2 rounded-full shadow"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default Chat;
